#!/bin/bash

# Benchmark script Ä‘á»ƒ Ä‘o hiá»‡u nÄƒng cÃ¡c trÆ°á»ng há»£p khÃ¡c nhau
# Táº¡o bÃ¡o cÃ¡o tá»± Ä‘á»™ng vá»›i metrics thá»±c táº¿

SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
PROJECT_DIR="$(cd "$SCRIPT_DIR/.." && pwd)"
BENCHMARK_DIR="$PROJECT_DIR/benchmark_results"
TIMESTAMP=$(date +%Y%m%d_%H%M%S)
RESULTS_FILE="$BENCHMARK_DIR/results_${TIMESTAMP}.json"

mkdir -p "$BENCHMARK_DIR"
export LD_LIBRARY_PATH="$PROJECT_DIR/onnxruntime-linux-x64-1.16.3/lib:$LD_LIBRARY_PATH"

BENCHMARK_DURATION=${BENCHMARK_DURATION:-30}
NUM_RUNS=${NUM_RUNS:-3}

echo "=========================================="
echo "ðŸš€ Benchmark Tool"
echo "=========================================="
echo "Duration: ${BENCHMARK_DURATION}s per test"
echo "Runs: ${NUM_RUNS} per test"
echo ""

# Get system info
CPU_MODEL=$(lscpu | grep 'Model name' | cut -d: -f2 | xargs)
CPU_CORES=$(nproc)
RAM=$(free -h | grep Mem | awk '{print $2}')
if command -v nvidia-smi &> /dev/null; then
    GPU_NAME=$(nvidia-smi --query-gpu=name --format=csv,noheader | head -1)
    CUDA_VER=$(nvidia-smi --query-gpu=cuda_version --format=csv,noheader | head -1)
else
    GPU_NAME="N/A"
    CUDA_VER="N/A"
fi

# Initialize JSON results
cat > "$RESULTS_FILE" << EOF
{
  "timestamp": "$TIMESTAMP",
  "system_info": {
    "cpu_model": "$CPU_MODEL",
    "cpu_cores": $CPU_CORES,
    "ram": "$RAM",
    "gpu_name": "$GPU_NAME",
    "cuda_version": "$CUDA_VER"
  },
  "config": {
    "duration": $BENCHMARK_DURATION,
    "num_runs": $NUM_RUNS
  },
  "results": []
}
EOF

# Test cases
declare -a TEST_CASES=(
    "Sequential Sobel + Sequential Pipeline:sequential:sequential"
    "OpenMP Sobel + Sequential Pipeline:sequential:openmp"
    "CUDA Sobel + Sequential Pipeline:sequential:cuda"
    "Sequential Sobel + Parallel Pipeline:parallel:sequential"
    "OpenMP Sobel + Parallel Pipeline:parallel:openmp"
    "CUDA Sobel + Parallel Pipeline:parallel:cuda"
)

run_test() {
    local test_name=$1
    local pipeline=$2
    local sobel=$3
    local run_num=$4
    
    echo "[$run_num/$NUM_RUNS] Testing: $test_name"
    
    # Set pipeline mode
    if [ "$pipeline" == "parallel" ]; then
        export USE_PARALLEL_PIPELINE=1
        PIPELINE_ARG="--parallel"
    else
        export USE_PARALLEL_PIPELINE=0
        PIPELINE_ARG="--sequential"
    fi
    
    # Start metrics collection in background
    METRICS_FILE="$BENCHMARK_DIR/metrics_${TIMESTAMP}_${test_name// /_}_run${run_num}.csv"
    "$SCRIPT_DIR/collect_metrics.sh" $$ $BENCHMARK_DURATION "$METRICS_FILE" &
    METRICS_PID=$!
    
    # Run test with timeout
    START_TIME=$(date +%s.%N)
    timeout ${BENCHMARK_DURATION}s "$PROJECT_DIR/test_onnx_models" $PIPELINE_ARG > /dev/null 2>&1 &
    TEST_PID=$!
    
    # Wait for test to complete
    wait $TEST_PID 2>/dev/null
    END_TIME=$(date +%s.%N)
    
    # Stop metrics collection
    kill $METRICS_PID 2>/dev/null
    wait $METRICS_PID 2>/dev/null
    
    # Calculate FPS (approximate - count frames processed)
    ELAPSED=$(echo "$END_TIME - $START_TIME" | bc)
    
    # Parse metrics
    if [ -f "$METRICS_FILE" ]; then
        AVG_CPU=$(awk -F',' 'NR>1 {sum+=$2; count++} END {if(count>0) print sum/count; else print 0}' "$METRICS_FILE")
        AVG_MEM=$(awk -F',' 'NR>1 {sum+=$3; count++} END {if(count>0) print sum/count; else print 0}' "$METRICS_FILE")
        AVG_GPU=$(awk -F',' 'NR>1 && $4!="N/A" {sum+=$4; count++} END {if(count>0) print sum/count; else print "N/A"}' "$METRICS_FILE")
    else
        AVG_CPU=0
        AVG_MEM=0
        AVG_GPU="N/A"
    fi
    
    # Estimate FPS (simplified - would need actual frame count from app)
    EST_FPS=$(echo "scale=2; $BENCHMARK_DURATION / $ELAPSED" | bc)
    
    echo "  FPS: ~$EST_FPS, CPU: ${AVG_CPU}%, Memory: ${AVG_MEM}MB, GPU: ${AVG_GPU}%"
    
    # Add to JSON (simplified - would use jq in real implementation)
    echo "{\"test_name\":\"$test_name\",\"pipeline_mode\":\"$pipeline\",\"sobel_mode\":\"$sobel\",\"run_num\":$run_num,\"fps\":$EST_FPS,\"cpu_usage\":$AVG_CPU,\"memory_mb\":$AVG_MEM,\"gpu_usage\":\"$AVG_GPU\"}" >> "$BENCHMARK_DIR/temp_results.json"
}

# Run all tests
for test_case in "${TEST_CASES[@]}"; do
    IFS=':' read -r test_name pipeline sobel <<< "$test_case"
    
    echo ""
    echo "â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”